mapper.select:
select * from member
where id=#{id}
and member_status = 'Y'

mapper.update:
update member
set pwd=#{newPwd}
where id=#{id}

update member
set member_status='N'
where id=#{id}

update member
set profile = #{profile}
where id = #{id}

mapper.select:
select *
from member
where member_status ='Y'

	and email=#{email} and name=#{name}


	and email=#{email} and id=#{id}

mapper.update:
update todo_list

	set status = #{status}


	set important = #{important}

where todo_num = #{todoNum}
